import { createSlice } from '@reduxjs/toolkit'

const noUser = {
    id: null,
    username: null,
    password: null,
    forename: null,
    surname: null
}

export const userDataSlice = createSlice({
    name: 'useData',
    initialState: {
        value: noUser
    },
    reducers: {
        setUserData: (state, action) => {
            return{
                ...state,
                id: action.payload[0],
                username: action.payload[1],
                password: action.payload[2],
                forename: action.payload[3],
                surname: action.payload[4],
            }
        },

        clearUserData: state => {
            return{
                ...state,
                id: null,
                username:null,
                password: null,
                forename: null,
                surname: null,
            }
        }
    }
})

export const {setUserData, clearUserData} = userDataSlice.actions

export default userDataSlice.reducer
