//import React, { useEffect, useState } from 'react'
import { useRef, useState, useEffect} from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { login, loginAsync, loginSlice, fullfilled, rejected } from './loginSlice'
//import styles from './Login.module.css'
import io from 'socket.io-client'
const socket = io.connect("http://localhost:3001"); 


export function Login() {

    const errorMessageSelector =  useSelector((state) => state.login.errorMessage)
    const forenameSelector =  useSelector((state) => state.login.forename)
    const surnameSelector =  useSelector((state) => state.login.surname)
    const dispatch = useDispatch()

    const userRef = useRef();
    const errRef = useRef();

    const [user, setUser] = useState('');
    const [pwd, setPwd] = useState('');
    const [errMsg, setErrMsg] = useState('');
    const [success, setSuccess] = useState(false);

    useEffect(() => {
        userRef.current.focus()
    }, [])

    useEffect(() => {
        setErrMsg('');
    }, [user, pwd])

    const logout = () => {
        setSuccess(false)
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        socket.emit("request_login", {user, pwd});
        socket.on("loginstatus", (result, forename, surname) => {
            if (result === true) {
                //console.log(result, forename, surname)
                dispatch(fullfilled([forename, surname]))
                setSuccess(true)
            } else {
                dispatch(rejected())
                console.log("test")
                setErrMsg(errorMessageSelector)
            }
        })
    }

    return (
        <> {success ? (
            <section>
                <h1>Hello </h1>
                 yfsg {forenameSelector} {surnameSelector}
                <br/>
                <p>
                    <button onClick={logout}>Logout</button>
                </p>
            </section>
        ): (
        <section>
            <p 
                ref={errorMessageSelector}
                className={errorMessageSelector? "errmsg": "offscreen"}
                aria-live="asserive">{errorMessageSelector}
            </p>
            <h1>Sign In</h1>
            <form>
                <label htmlFor="username">Username</label>
                <input 
                    type="text"
                    id="username"
                    ref={userRef}
                    autoComplete= "off"
                    onChange={(e) => setUser(e.target.value)}
                    value={user}
                    required
                />
                <label htmlFor="password">Password</label>
                <input 
                    type="password"
                    id="password"
                    onChange={(e) => setPwd(e.target.value)}
                    value={pwd}
                    required
                />
                <button onClick={handleSubmit}>Sign In</button>
            </form>
        </section>
        )}
        </>
    )

}
