let data = {     
    isLoggedIn: false,
    errorMessage: null,
    isLoading: false,
    forename: "null",
    surname: "null"}

export const loginSlice = createSlice({
    name: 'login',
    initialState: {
        value: data
    },
    reducers: {
        /*fullfilled: state => {
                state.value.isLoggedIn = 
        },*/

        rejected: state => {
                /*state.value.isLoggedIn =  "false",
                state.value.errorMessage =  "Unauthorized",
                state.value.isLoading =  false*/
        },
        fullfilled: (state, action) => {
                return{
                    ...state,
                    isLoading: true,
                    errorMessage: "test",
                    isLoading: false,
                    forename: action.payload[0]
                }
                /*state.value.isLoggedIn = true,
                state.value.errorMessage = "test",
                state.value.isLoading = false,
                state.value.forename = action.payload*/
        },
        clearName: (state) => {
            /*state.value.isLoggedIn = false,
            state.value.errorMessage = null,
            state.value.isLoading = false,
            state.value.forename = null*/
        }
    },
    /*extraReducers: (builder) => {
        builder.addCase(loginAsync.pending, (state, action) => {
            console.log("PENDING")
            return {
                ...state,
                isLoading: true
            }
        })
        builder.addCase(loginAsync.fulfilled, (state, action) => {
            return {
                ...state,
                isLoggedIn: true,
                isLoading: false
            }
        })
        builder.addCase(loginAsync.rejected, (state, action) => {
            return {
                ...state,
                isLoggedIn: false,
                errorMessage: action.payload,
                isLoading: false
            }
        })
    }*/
})

// Action creators are generated for each case reducer function
export const { fullfilled, rejected, clearName } = loginSlice.actions

//export { loginAsync };

export default loginSlice.reducer
